require "main"

-- This file includes helper functions.

-- Balanced for small enemy colliding with player
function hitDetect(element_x, element_y)
    
    -- Stores into variables to improve readability of the funtion
    local element_x_left = element_x.x
    local element_x_right = element_x.x + element_x.width
    local element_x_bottom = element_x.y + element_x.height
    local element_x_top = element_x.y
    
    local element_y_left = element_y.x
    local element_y_right = element_y.x + element_y.width
    local element_y_bottom = element_y.y + element_y.height
    local element_y_top = element_y.y
    
    if element_y.image == coinImage then
        element_y_right = element_y_right + 45
    elseif element_y.imageMain == imgB then
        element_y_bottom = element_y_bottom - 30
        element_y_right = element_y_right + 25
    end
    
    if element_y.imageMain == imgM then
        if user.y < element_y.y and element_x_right < element_y_left then
            element_y_left = element_y_left + 10
            element_y_right = element_y_right + 30
            element_y_bottom = element_y_bottom + 18
        end
    elseif element_y.image == fMoveSheet then
        element_y_Top = element_y_top
        element_y_left = element_y_left + 50
        element_y_right = element_y_right + 50
    end
    
    if element_x_bottom > element_y_top + 15 and element_x_top < element_y_bottom
    and jumpBool == false and element_x_right > element_y_left + 15 and element_x_left < element_y_right - 60 then
            
        --enemies from classes
        if element_y.imageMain == imgM and element_x_bottom > element_y_top and element_x_top < element_y_top - 80 then
            element_y.eAlive = false
            return true
        elseif element_y.imageMain == imgM and element_x_bottom > element_y_top and element_x_bottom < element_y_bottom + 10 then
            element_y.eAlive  = true
            return true
        elseif element_y.imageMain == imgB and element_x_bottom > element_y_top and element_x_top < element_y_top - 80 then
            element_y.eAlive = false
            return true
        elseif element_y.imageMain == imgB and element_x_bottom > element_y_top and element_x_bottom < element_y_bottom + 10 then
            element_y.eAlive = true
            return true
        elseif element_y.image == fMoveSheet and element_x_bottom > element_y_top and element_x_top < element_y_top then
            element_y.eAlive = false
            return true
        elseif element_y.image == fMoveSheet and element_x_bottom > element_y_top and element_x_bottom < element_y_bottom + 10 then
            element_y.eAlive = true
            return true
        end
    end
    
    -- Collision detection
    
    
    if element_x_right > element_y_left + 15
    and element_x_left < element_y_right - 60
    and element_x_bottom > element_y_top + 15
    and element_x_top < element_y_bottom then
        return true
    else
        return false
    end
end

